//             .then((res) => {
//                 dispatch(loginSuccessAction(res.data));
//             })
//             .catch((err) => {
//                 dispatch(loginFailureAction(err));
//             })
//     }
// }


export const loginRequestAction = (data) => {
    return {
        type: 'LOG_IN_REQUEST',
        data,
    }
}


export const logoutRequestAction = () => {
    return {
        type: 'LOG_OUT_REQUEST',
    }
}


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN_REQUEST':
            return {
                ...state,
                isLoggedIn: true,
            };
        case 'LOG_IN_SUCCESS':
            return {
                ...state,
                isLoggedIn: true,
                me: { ...action.data, nickname: 'defian' },
            };
        case 'LOG_IN_FAILURE':
            return {
                ...state,
                isLoggedIn: true,
                me: action.data,
            };
        case 'LOG_OUT_REQUEST':
            return {
                ...state,
                isLoggedIn: false,
                me: null,
            }
        case 'LOG_OUT_SUCCESS':
            return {
                ...state,
                isLoggedIn: false,
                me: null,
            }
        case 'LOG_OUT_FAILURE':
            return {
                ...state,
                isLoggedIn: false,
                me: null,
            }
        default:
            return state;
    }
};


export default reducer;
